select c.field_spch_prenom_value  as prenom, c.field_spch_nom_value  as nom, t.field_spch_research_cov_keyword_value as theme from node n LEFT JOIN content_type_spire_chercheur_type c on c.nid=n.nid  left join content_field_spch_research_cov_keyword t on t.nid=n.nid where n.type like "spire_chercheur_type" and n.language like "fr" and t.field_spch_research_cov_keyword_value IS NOT NULL ORDER BY nom;
